mapper.select:

select no,id,email,password,nickname,image
from user
where no=#{no};


select count(id)
  from user 
 where id = #{id};


select count(email)
  from user 
 where email = #{email};

mapper.insert:

insert 
  into user(no, id, email, password, nickname, image)
 value (null, #{id}, #{email}, #{password}, #{nickname}, #{image} );
